+/**
+ * We're testing GD2 image files which report illegal chunk counts. These should
+ * not cause integer overflows or other issues, but instead simply fail to be
+ * loaded.
+ *
+ * See also <https://github.com/libgd/libgd/issues/354>.
+ */
+
+
+#include ""gd.h""
+#include ""gdtest.h""
+
+
+int main()
+{
+    gdImagePtr im;
+    FILE *fp;
+
+    fp = gdTestFileOpen2(""gd2"", ""bug00354a.gd2"");
+    gdTestAssert(fp != NULL);
+    im = gdImageCreateFromGd2(fp);
+    gdTestAssert(im == NULL);
+    fclose(fp);
+
+    fp = gdTestFileOpen2(""gd2"", ""bug00354b.gd2"");
+    gdTestAssert(fp != NULL);
+    im = gdImageCreateFromGd2(fp);
+    gdTestAssert(im == NULL);
+    fclose(fp);
+
+    return gdNumFailures();
+}""}<_**next**_>{""sha"": ""5060bfde3aad5687c0a962a8eddf86c0a83dded9"", ""filename"": ""tests/gd2/bug00354a.gd2"", ""status"": ""added"", ""additions"": 0, ""deletions"": 0, ""changes"": 0, ""blob_url"": ""https://github.com/libgd/libgd/blob/69d2fd2c597ffc0c217de1238b9bf4d4bceba8e6/tests/gd2/bug00354a.gd2"", ""raw_url"": ""https://github.com/libgd/libgd/raw/69d2fd2c597ffc0c217de1238b9bf4d4bceba8e6/tests/gd2/bug00354a.gd2"", ""contents_url"": ""https://api.github.com/repos/libgd/libgd/contents/tests/gd2/bug00354a.gd2?ref=69d2fd2c597ffc0c217de1238b9bf4d4bceba8e6""}<_**next**_>{""sha"": ""8600126becb28c0962812c00fc6f2919b9f2ba80"", ""filename"": ""tests/gd2/bug00354b.gd2"", ""status"": ""added"", ""additions"": 0, ""deletions"": 0, ""changes"": 0, ""blob_url"": ""https://github.com/libgd/libgd/blob/69d2fd2c597ffc0c217de1238b9bf4d4bceba8e6/tests/gd2/bug00354b.gd2"", ""raw_url"": ""https://github.com/libgd/libgd/raw/69d2fd2c597ffc0c217de1238b9bf4d4bceba8e6/tests/gd2/bug00354b.gd2"", ""contents_url"": ""https://api.github.com/repos/libgd/libgd/contents/tests/gd2/bug00354b.gd2?ref=69d2fd2c597ffc0c217de1238b9bf4d4bceba8e6""}","_gd2GetHeader (gdIOCtxPtr in, int *sx, int *sy,

