+  if (!base::android::IsSelectiveJniRegistrationEnabled(env) &&
+      !RegisterNonMainDexNatives(env)) {
+    return -1;
+  }
+
+  if (!RegisterMainDexNatives(env)) {
+    return -1;
+  }
+
+  if (!android::OnJNIOnLoadRegisterJNI(env)) {
+    return -1;
